Scripts.OnLoad:
local closeBtn = self.CloseButton if closeBtn then closeBtn:ClearAllPoints() closeBtn:SetPoint("TOPRIGHT", self, "TOPRIGHT", 4, 4) end
-- hide scrollbar visuals (keep wheel/scrollChild functional) local sb = self.ScrollFrame.ScrollBar if sb then local up = _G[sb:GetName().."ScrollUpButton"] local down = _G[sb:GetName().."ScrollDownButton"] sb:Hide(); sb:SetAlpha(0); sb:EnableMouse(false) if up then up:Hide(); up:SetAlpha(0) end if down then down:Hide(); down:SetAlpha(0) end end
self:SetFrameLevel(5); self:RegisterForDrag("LeftButton");
